var express = require('express');
var router = express.Router();
var dateFormat = require('dateformat');    
var database = require('./dao');
var dao = new database.Dao();    
var sessionChecker = require('./security');
var appresource = {
    Title:'MsbOfflineRead',
    Heading:'MsbOffline',
    Subscriber:{label :'Subscriber'},
    Messageid:{label :'Messageid'},
    Creationdate:{label :'Creationdate'},
    Messagesize:{label :'Messagesize'},
    Stanza:{label :'Stanza'},
    }

router.get('/', sessionChecker, function(req, res, next) {
    var jsonObj = req.query;
    jsonObj.rowid = 0;
    req.session.previouspath = 'readMsbOffline?'+req._parsedUrl.query;
    parameter = {
        session: req.session,
        cookies: req.cookies,
        resource: appresource,
        dateFormat: dateFormat,
    }
    var readMsbOffline = new Promise((resolve, reject) => {
        setTimeout(() => {
            dao.readMsbOffline(jsonObj,function(err,rows) {
                if(err) res.status(err.code).end();
                else{
                    if(rows[0]===undefined) res.redirect('/searchMsbOffline');
                    else{ 
                        jsonObj = rows[0];
                        resolve(jsonObj);
                    }
                }
            });
        },1000);
    });
    
    var sendResult = (results) => {
        console.log(jsonObj)
        parameter.record = jsonObj;        
        res.render('MsbOfflineReadPage',parameter);
    }    
    Promise.all([readMsbOffline]).then(sendResult);
});

module.exports = router;
