var express = require('express');
var router = express.Router();
var dateFormat = require('dateformat');    
var database = require('./dao');
var dao = new database.Dao();    
var sessionChecker = require('./security');
var appresource = {
    Title:'MsbPubsubsubscriberRead',
    Heading:'MsbPubsubsubscriber',
    Serviceid:{label :'Serviceid'},
    Nodeid:{label :'Nodeid'},
    Jid:{label :'Jid'},
    Associationtype:{label :'Associationtype'},
    }

router.get('/', sessionChecker, function(req, res, next) {
    var jsonObj = req.query;
    jsonObj.rowid = 0;
    req.session.previouspath = 'readMsbPubsubsubscriber?'+req._parsedUrl.query;
    parameter = {
        session: req.session,
        cookies: req.cookies,
        resource: appresource,
        dateFormat: dateFormat,
    }
    var readMsbPubsubsubscriber = new Promise((resolve, reject) => {
        setTimeout(() => {
            dao.readMsbPubsubsubscriber(jsonObj,function(err,rows) {
                if(err) res.status(err.code).end();
                else{
                    if(rows[0]===undefined) res.redirect('/searchMsbPubsubsubscriber');
                    else{ 
                        jsonObj = rows[0];
                        resolve(jsonObj);
                    }
                }
            });
        },1000);
    });
    
    var sendResult = (results) => {
        console.log(jsonObj)
        parameter.record = jsonObj;        
        res.render('MsbPubsubsubscriberReadPage',parameter);
    }    
    Promise.all([readMsbPubsubsubscriber]).then(sendResult);
});

module.exports = router;
